import React from 'react';
import { FaSearch, FaLine } from 'react-icons/fa';
import CommunityContent from './CommunityContent';

const CommunityCards = () => {
  return (
    <div className="p-6">
      <div className="mb-4 flex justify-between items-center">
        <p>Sort by :
          <select className="ml-2 p-2 border border-gray-300 rounded">
            <option value="Recent-Activity">Recent Activity</option>
            <option value="A-Z">A - Z</option>
            <option value="Member-Count">Member Count</option>
          </select>
        </p>
        <div className="flex items-center space-x-4">
          <FaLine className="text-gray-400" />
          <FaSearch className="text-gray-500" />
        </div>
      </div>

      <div className="flex space-x-6 mb-6">
        <ul className="flex space-x-4 text-orange-600 font-semibold">
          <li>All (3)</li>
          <li>My Groups</li>
          <li>Suggested Groups</li>
        </ul>
      </div>

      {/* Grid layout for cards */}
      <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-6">
        {CommunityContent.map((card) => (
          <div key={card.id} className="bg-orange-600 p-4 rounded-lg shadow-lg relative">
            {/* Image Section */}
            <div className="w-full h-48 overflow-hidden rounded-lg">
              <img 
                src={card.image} 
                alt={card.title} 
                className="w-full h-full object-cover transition-transform duration-500 ease-in-out transform hover:scale-105" 
              />
            </div>

            {/* Title and Description */}
            <div className="bg-white p-4 mt-4 rounded-lg">
              <h1 className="text-xl font-bold text-gray-800">{card.description}</h1>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}

export default CommunityCards;
