import React from 'react';
import gymImage from './images/DALL·E 2024-10-15 17.30.42 - A high-quality image of a modern gym environment with sleek workout equipment such as treadmills, weight racks, and dumbbells, all placed neatly. The .webp';  // Update the path as necessary

const Home = () => {
  return (
    <div
      className="relative h-screen bg-cover bg-center"
      style={{ backgroundImage: `url(${gymImage})` }}  // Apply the image here
    >
      <div className="absolute inset-0 bg-black opacity-50"></div> {/* Dark overlay */}
      <div className="relative z-10 flex flex-col items-center justify-center h-full text-center text-white">
        <h1 className="text-5xl font-bold mb-4">Welcome to Our E-Commerce Site</h1>
        <button className="bg-blue-500 hover:bg-blue-600 text-white font-semibold py-2 px-6 rounded">
          Get Started
        </button>
      </div>
    </div>
  );
};

export default Home;
