import React, { useEffect, useState } from 'react';
import gymImage from './images/sport.webp';
import CardsHome from './CardsHome';
import run from './images/'; // Correct import of the run image

const Home = () => {
  const [scrollPos, setScrollPos] = useState(0);

  // Update scroll position when user scrolls
  useEffect(() => {
    const handleScroll = () => {
      setScrollPos(window.scrollY);
    };

    window.addEventListener('scroll', handleScroll);

    // Cleanup the event listener on component unmount
    return () => {
      window.removeEventListener('scroll', handleScroll);
    };
  }, []);

  return (
    <div>
      {/* Background section */}
      <div
        className="relative h-screen bg-fixed bg-cover bg-center"
        style={{ backgroundImage: `url(${gymImage})` }}
      >
        <div className="absolute inset-0 bg-black opacity-50"></div>

        <div className="relative z-10 flex flex-col items-center justify-center h-full text-center text-white">
          <h1 className="text-6xl font-bold mb-4">IT DOESN'T GET EASIER.</h1>
          <h1 className="text-6xl font-bold mb-4">YOU GET BETTER.</h1>
          <button className="bg-transparent hover:bg-white text-white font-semibold py-2 px-6 border border-white rounded opacity-75 hover:text-black transition duration-300">
            View All Challenges
          </button>
        </div>
      </div>
      
      {/* CardsHome Component */}
      <CardsHome />

      {/* Section with the runner image and text */}
      <div className="flex space-y-14 gap-24 mt-10 relative">
        {/* Runner Image with horizontal scroll movement */}
        <img 
          src={run} 
          alt="Running athlete" 
          className="w-1/2" 
          style={{
            position: 'fixed',  // Keeps the image fixed while scrolling
            left: `${scrollPos * 0.1}px`, // Moves the image left based on scroll position
            transition: 'transform 0.1s ease-out'
          }}
        />

        {/* Story section */}
        <div className="w-52 ml-auto">
          <h4 className="font-bold">__ My Story</h4>
          <p>I'm a paragraph. Click here to add your own text and edit me. It’s easy. Just click “Edit Text” or double click me to add your own content and make changes to the font.</p>
          <button className="mt-4 border border-white text-white bg-orange-600 hover:bg-black text-sm font-semibold px-4 py-2 rounded transition-colors duration-300 w-52">
            Read More
          </button>
        </div>
      </div>
    </div>
  );
};

export default Home;
