import React, { useEffect, useState } from 'react';
import gymImage from './images/sport.webp';
import CardsHome from './CardsHome';

const Home = () => {
  const [scrollDirection, setScrollDirection] = useState('');

  useEffect(() => {
    let lastScrollY = window.pageYOffset;

    const handleScroll = () => {
      const currentScrollY = window.pageYOffset;

      if (currentScrollY > lastScrollY) {
        setScrollDirection('right'); // Scroll down
      } else {
        setScrollDirection('left'); // Scroll up
      }

      lastScrollY = currentScrollY;
    };

    window.addEventListener('scroll', handleScroll);

    return () => {
      window.removeEventListener('scroll', handleScroll);
    };
  }, []);
  return (
    <div>
      <div
        className="relative h-screen bg-fixed bg-cover bg-center"
        style={{ backgroundImage: `url(${gymImage})` }}
      >
        <div className="absolute inset-0 bg-black opacity-50"></div>


        <div className="relative z-10 flex flex-col items-center justify-center h-full text-center text-white">
          <h1 className="text-6xl font-bold mb-4">IT DOESN'T GET EASIER.</h1>
          <h1 className="text-6xl font-bold mb-4">YOU GET BETTER.</h1>
          <button className="bg-transparent hover:bg-white text-white font-semibold py-2 px-6 border border-white rounded opacity-75 hover:text-black transition duration-300">
            View All Challenges
          </button>
        </div>
      </div>
      <CardsHome/>

      <div className="flex flex-col justify-center items-center min-h-screen">
      <div className="flex justify-center items-center gap-6 w-full">
        <img
          src="https://tse2.mm.bing.net/th?id=OIP.2L5JoNEm89rKeJkpNjxIkAHaEj&pid=Api&P=0&h=220"
          alt="My Story"
          className={`w-1/2 h-auto transition-transform duration-500 ${
            scrollDirection === 'right' ? 'translate-x-full' : '-translate-x-full'
          }`}
          style={{ position: 'sticky', top: '20px' }}
        />
        <div className="w-1/2">
          <h4 className="text-2xl font-bold">__ My Story</h4>
          <p className="mt-4 text-lg">
            I'm a paragraph. Click here to add your own text and edit me. It’s easy. Just click “Edit Text” or double
            click me to add your own content and make changes to the font.
          </p>
          <button className="mt-4 border border-white text-white bg-orange-600 hover:bg-black text-sm font-semibold px-4 py-2 rounded transition-colors duration-300 w-52">
            Read More
          </button>
        </div>
      </div>
    </div>
    </div>

  );
}

export default Home;
