import React from 'react';

const Home = () => {
  return (
    <div className="relative h-screen bg-cover bg-center" style={{ backgroundImage: 'url(https://tse4.mm.bing.net/th?id=OIP.2XaNxGx8Cein6UvrbpkisQHaEK&pid=Api&P=0&h=220)' }}>
  <div className="absolute inset-0 bg-black opacity-50"></div> {/* This is for the dark overlay effect */}

  <div className="relative z-10 flex flex-col items-center justify-center h-full text-center text-white">
    <h1 className="text-5xl font-bold mb-4">Welcome to Our E-Commerce Site</h1>
    <button className="bg-blue-500 hover:bg-blue-600 text-white font-semibold py-2 px-6 rounded">
      Get Started
    </button>
  </div>
</div>

  );
}

export default Home;
