import React from 'react';
import { Link } from 'react-router-dom';
import { useForm } from 'react-hook-form';
import { yupResolver } from '@hookform/resolvers/yup';
import * as yup from 'yup';
import { toast, ToastContainer } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';

const schema = yup.object().shape({
  firstName: yup.string().required('First name is required'),
  lastName: yup.string().required('Last name is required'),
  email: yup.string().email('Invalid email format').required('Email is required'),
  password: yup
    .string()
    .min(8, 'Password must be at least 8 characters long')
    .matches(/[A-Z]/, 'Password must have at least one uppercase letter')
    .matches(/[a-z]/, 'Password must have at least one lowercase letter')
    .matches(/[!@#$%^&*(),.?":{}|<>]/, 'Password must have at least one special character')
    .required('Password is required'),
  confirmPassword: yup
    .string()
    .oneOf([yup.ref('password'), null], 'Passwords do not match')
    .required('Confirm Password is required'),
});

const Signup = () => {
  const { register, handleSubmit, formState: { errors } } = useForm({
    resolver: yupResolver(schema),
  });

  const onSubmit = async (data) => {
    try {
      const response = await fetch('https://freelancers.runasp.net/Auth/SignUp', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(data),
      });

      if (response.ok) {
        toast.success('Account created successfully!');
      } else if (response.status === 409) {
        toast.error('Account with this email already exists. Please use another email.');
      } else {
        toast.error('Failed to create account. Please try again.');
      }
    } catch (error) {
      console.error('Error:', error);
      toast.error('An error occurred. Please try again.');
    }
  };

  return (
    <div>
        <div className='MainSignup'>
      <div className="header">
      </div>
      <div className="formContent">
        <div className='mainForm'>
          <h2>Signup To Your Account</h2>
          <p className='underButton'>See what is going on with your business</p>
          <button className='button-google'>
            <img src="src/assets/search.png" alt=""></img>
            <div className="green-circle"></div>
            Continue with Google
          </button>
          <p className='signEmail'>------------ or Sign Up with Email ------------</p>

          <div>
      <form className="FormDesign" onSubmit={handleSubmit(onSubmit)}>
        <div className='firstList'>
          <div className="inputGroup">
            <label htmlFor='firstName'>First Name</label>
            <input type="text" id='firstName' {...register('firstName')} placeholder="First Name" className='button-google' />
            {errors.firstName && <p className="error">{errors.firstName.message}</p>}
          </div>
          <div className="inputGroup">
            <label htmlFor='lastName'>Last Name</label>
            <input type="text" id='lastName' {...register('lastName')} placeholder="Last Name" className='button-google' />
            {errors.lastName && <p className="error">{errors.lastName.message}</p>}
          </div>
        </div>
        <div className="formList">
          <label htmlFor="email">Email</label>
          <input type="email" id="email" {...register('email')} placeholder="mail@abc.com" className='button-google' />
          {errors.email && <p className="error">{errors.email.message}</p>}

          <label htmlFor='password'>Password</label>
          <input type="password" id="password" {...register('password')} placeholder="***********" className='button-google' />
          {errors.password && <p className="error">{errors.password.message}</p>}

          <label htmlFor='password'>Confirm Password</label>
          <input type="password" id="confirmPassword" {...register('confirmPassword')} placeholder="Confirm your Password" className='button-google' />
          {errors.confirmPassword && <p className="error">{errors.confirmPassword.message}</p>}

          <div className='ForgotPass'>
            <div>
              <input type='checkbox' id="rememberMe"></input>
              <label htmlFor="rememberMe">Remember Me</label>
            </div>
            <Link to='/forgot'>Forgot Password?</Link>
          </div>
        </div>
        <button type="submit" className='button-google lastButton'>Signup</button>
      </form>

    </div>
          
          <div className='HaveAccount'>
            <p>Already have an account? <Link to='/login'>Sign in</Link></p>
          </div>
        </div>
      </div>
    </div>
    </div>
  );
}

export default Signup;
