import React from 'react';
import { FaSearch, FaLine } from 'react-icons/fa';
import CommunityContent from './CommunityContent';
import { useParams } from 'react-router-dom';

const CommunityCards = () => {
  const { id } = useParams();
  const card = CommunityContent.find((card) => card.id === parseInt(id)) || CommunityContent[0]; // Fallback to first card if no id match

  return (
    <div className="p-6">
      <div className="mb-4 flex justify-between items-center">
        <p>Sort by :
          <select className="ml-2 p-2 border border-gray-300 rounded">
            <option value="Recent-Activity">Recent Activity</option>
            <option value="A-Z">A - Z</option>
            <option value="Member-Count">Member Count</option>
          </select>
        </p>
        <div className="flex items-center space-x-4">
          <FaLine className="text-gray-400" />
          <FaSearch className="text-gray-500" />
        </div>
      </div>

      <div className="flex space-x-6 mb-6">
        <ul className="flex space-x-4 text-orange-600 font-semibold">
          <li>All (3)</li>
          <li>My Groups</li>
          <li>Suggested Groups</li>
        </ul>
      </div>

      {/* Flex container for Image and Title */}
      <div className="flex flex-col md:flex-row items-center justify-center mb-52 mx-auto bg-orange-600 h-80 p-6 rounded-lg shadow-lg relative">
        {/* Image Section */}
        <div className="w-full md:w-1/2 h-full overflow-hidden">
          <img src={card.image} alt={card.title} className="w-full h-full object-cover transition-transform duration-500 ease-in-out transform hover:scale-110" />
        </div>

        {/* Title Section */}
        <div className="w-full md:w-1/2 p-4 bg-white flex items-center justify-center">
          <h1 className="text-4xl font-bold text-gray-800 text-center">{card.description}</h1>
        </div>
      </div>
    </div>
  );
}

export default CommunityCards;
